#include<iostream>
#include<string>
using namespace std;

string add(string a, string b)
{	int a_len, b_len, max_len, carry;
	int a_array[100010]={0}, b_array[100010]={0};
	string result;

	a_len = a.length();
	b_len = b.length();
	carry = 0;
	for (int i=0; i<a_len; ++i)
		a_array[i] = a[a_len-i-1] - '0';
	for (int i=0; i<b_len; ++i)
		b_array[i] = b[b_len-i-1] - '0';
	max_len = a_len>b_len?a_len:b_len;
	for (int i=0; i<=max_len; ++i)
	{	a_array[i] += carry;
		carry = (a_array[i] + b_array[i]) / 10;
		a_array[i] = (a_array[i] + b_array[i]) % 10;
	}
	if (a_array[max_len]==0)
		--max_len;
	for (int i=max_len; i>=0; --i)
		result += a_array[i]+'0';
	return result;
}
